import * as React from 'react';
import { animated, useSpring } from 'react-spring/web.cjs';

interface Props {
	orbitStyle?: (defaultStyle: CSSProperties | CreateCSSProperties<{}>) => CSSProperties | CreateCSSProperties<{}>;
