}

function getFinalSatellitePosition(
  index: number,
  satelliteCount: number,
  width: number,
  height: number,
  planetWidth: number,
  planetHeight: number,
  orbitRadius: number,
  rotation: number,
  orientation: "DEFAULT" | "INSIDE" | "OUTSIDE" | "READABLE" | undefined
